package week1.day2;

public class LearnConditionStatment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x=10;
		int y=20;
		
		if(x>y)
		{
			System.out.println("True");
		}
		else if (x ==y)
		{
			System.out.println("Value are same");
		}
		else 
		{
			System.out.println("False");
		}


	}

}
